df_puna |&gt; 
  select(indice_tiempo, tipo, plazas) |&gt; 
  filter(indice_tiempo %in% c(2021, 2022)) |&gt; 
  mutate(tipo_recod = case_when(tipo == "Hoteleros" ~ "Hoteleros",
